import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { ReactComponent as HomeIcon } from '../assets/home.svg';
import { ReactComponent as MapIcon } from '../assets/map_act.svg';
import { ReactComponent as MyIcon } from '../assets/my.svg';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background: linear-gradient(180deg, #87CEEB 0%, #98FB98 100%);
  font-family: 'Pretendard', sans-serif;
`;

const CardContainer = styled.div`
  width: 440px;
  height: 956px;
  background: white;
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const MapContainer = styled.div`
  flex: 1;
  width: 100%;
  position: relative;
`;

const Navbar = styled.nav`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  height: 80px;
  background: white;
  border-top: 1px solid #e0e0e0;
  display: flex;
  justify-content: space-around;
  align-items: center;
  z-index: 1000;
`;

const NavItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  padding: 8px;

  svg {
    width: 24px;
    height: 24px;
    margin-bottom: 4px;
  }

  span {
    font-size: 12px;
    color: ${props => props.active ? '#007AFF' : '#666'};
  }
`;

const WeatherMap = () => {
  const navigate = useNavigate();

  useEffect(() => {
    // 카카오맵 스크립트 로드
    const script = document.createElement('script');
    script.src = '//dapi.kakao.com/v2/maps/sdk.js?appkey=YOUR_KAKAO_MAP_API_KEY&autoload=false';
    script.async = true;

    script.onload = () => {
      window.kakao.maps.load(() => {
        const container = document.getElementById('map');
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.9780), // 서울 시청
          level: 3
        };

        const map = new window.kakao.maps.Map(container, options);

        // 마커 추가
        const marker = new window.kakao.maps.Marker({
          position: new window.kakao.maps.LatLng(37.5665, 126.9780)
        });

        marker.setMap(map);
      });
    };

    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <div id="map" style={{ width: '100%', height: '100%' }}></div>
        </MapContainer>

        <Navbar>
          <NavItem onClick={() => navigate('/')}>
            <HomeIcon />
            <span>홈</span>
          </NavItem>
          <NavItem active onClick={() => navigate('/weather-map')}>
            <MapIcon />
            <span>지도</span>
          </NavItem>
          <NavItem onClick={() => navigate('/my')}>
            <MyIcon />
            <span>마이</span>
          </NavItem>
        </Navbar>
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
