import React, { useEffect } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-family: 'Pretendard', sans-serif;
`;

const CardContainer = styled.div`
  width: 440px;
  height: 956px;
  border-radius: 5px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const MapContainer = styled.div`
  flex: 1;
  width: 100%;
  position: relative;
  min-height: 400px;
`;

const MapDiv = styled.div`
  width: 100%;
  height: 100%;
  min-height: 400px;
`;

const WeatherMap = () => {
  useEffect(() => {
    console.log('WeatherMap component mounted');

    // 카카오맵 스크립트가 이미 로드되어 있는지 확인
    if (window.kakao && window.kakao.maps) {
      console.log('Kakao maps already loaded');
      initializeMap();
    } else {
      console.log('Loading Kakao maps script...');
      // 카카오맵 스크립트 로드
      const script = document.createElement('script');
      script.src = '//dapi.kakao.com/v2/maps/sdk.js?appkey=65c5f877dec8e4d2cbf95afcc51c3839&autoload=false';
      script.async = true;

      script.onload = () => {
        console.log('Kakao maps script loaded');
        window.kakao.maps.load(() => {
          console.log('Kakao maps initialized');
          initializeMap();
        });
      };

      script.onerror = (error) => {
        console.error('Failed to load Kakao maps script:', error);
      };

      document.head.appendChild(script);

      return () => {
        if (document.head.contains(script)) {
          document.head.removeChild(script);
        }
      };
    }
  }, []);

  const initializeMap = () => {
    console.log('Initializing map...');

    // DOM이 완전히 렌더링될 때까지 잠시 대기
    setTimeout(() => {
      const container = document.getElementById('map');
      console.log('Map container:', container);

      if (container) {
        console.log('Container dimensions:', container.offsetWidth, 'x', container.offsetHeight);

        // 컨테이너 크기가 0인 경우 강제로 크기 설정
        if (container.offsetWidth === 0 || container.offsetHeight === 0) {
          container.style.width = '100%';
          container.style.height = '400px';
          console.log('Forced container dimensions');
        }

        const options = {
          center: new window.kakao.maps.LatLng(33.450701, 126.570667), // 제주도 중심
          level: 3
        };

        try {
          const map = new window.kakao.maps.Map(container, options);
          console.log('Map created successfully');

          // 마커 추가
          const marker = new window.kakao.maps.Marker({
            position: new window.kakao.maps.LatLng(33.450701, 126.570667)
          });

          marker.setMap(map);
          console.log('Marker added');
        } catch (error) {
          console.error('Error creating map:', error);
        }
      } else {
        console.error('Map container not found');
      }
    }, 100);
  };

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <MapDiv id="map"></MapDiv>
        </MapContainer>
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
