import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-family: 'Pretendard', sans-serif;
`;

const CardContainer = styled.div`
  width: 440px;
  height: 956px;
  border-radius: 5px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  display: flex;
  flex-direction: column;
`;

const MapContainer = styled.div`
  flex: 1;
  width: 100%;
  min-height: 400px;
  position: relative;
`;

const MapDiv = styled.div`
  width: 100%;
  height: 956px; 
  min-height: 400px;
`;

const UserTextContainer = styled.div`
  position: absolute;
  top: 50%;
  right: 20px;
  transform: translateY(-50%);
  border-radius: 25px;
  background: rgba(0, 99, 178, 0.30);
  background: color(display-p3 0.1543 0.3804 0.6762 / 0.30);
  backdrop-filter: blur(6.150000095367432px);
  width: 248px;
  height: 247px;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  box-sizing: border-box;
  z-index: 10;
`;

const UserText = styled.div`
  color: #fff;
  font-family: 'Pretendard', sans-serif;
  font-size: 18px;
  line-height: 1.4;
  text-align: center;
  word-break: keep-all;
  overflow-wrap: break-word;
`;

const WeatherMap = () => {
  const [userText, setUserText] = useState('');
  const [hasUserText, setHasUserText] = useState(false);

  useEffect(() => {
    // localStorage에서 사용자 텍스트 확인
    const savedText = localStorage.getItem('userWeatherText');
    const hasText = localStorage.getItem('hasUserText');

    if (savedText && hasText === 'true') {
      setUserText(savedText);
      setHasUserText(true);
    }
  }, []);

  useEffect(() => {
    const initializeMap = () => {
      const container = document.getElementById('map');
      if (!container) {
        console.error('Map container not found');
        return;
      }

      const options = {
        center: new window.kakao.maps.LatLng(37.296442644565175, 126.83532389153223),
        level: 3,
      };

      const map = new window.kakao.maps.Map(container, options);

      const marker = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(33.450701, 126.570667),
      });

      marker.setMap(map);
    };

    const loadKakaoMapScript = () => {
      if (window.kakao && window.kakao.maps) {
        initializeMap();
        return;
      }

      if (document.getElementById('kakao-map-script')) {
        return;
      }

      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=236caf5234baf8221c0e94c739ba6412&autoload=false`;
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(() => {
          initializeMap();
        });
      };

      script.onerror = (error) => {
        console.error('Kakao map script load error:', error);
      };

      document.head.appendChild(script);
    };

    const timer = setTimeout(loadKakaoMapScript, 300);
    return () => clearTimeout(timer);
  }, []);

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <MapDiv id="map" />
          {hasUserText && (
            <UserTextContainer>
              <UserText>{userText}</UserText>
            </UserTextContainer>
          )}
        </MapContainer>
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
