import React, { useState } from 'react';
import styled from 'styled-components';
import writebackground from '../assets/writebackground.png';
import WriteButton from '../components/WriteButton';
import { useNavigate, useLocation } from 'react-router-dom';

const Container = styled.div`
  width: 440px;
  height: 956px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url(${writebackground});
  background-size: cover;
  background-position: center;
`;

const Card = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const BottomSheet = styled.div`
  width: 440px;
  height: ${props => props.showOnlyTop ? '30px' : '413px'};
  background: rgba(155, 155, 155, 0.39);
  flex-shrink: 0;
  box-shadow: 0px -1px 4px 0px rgba(0, 0, 0, 0.25);
  border-radius: 45px 45px 0px 0px;
  backdrop-filter: blur(15.6px);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  z-index: 100;
  overflow: hidden;
  transition: height 0.3s;
`;

const BottomSheetHandle = styled.div`
  width: 72px;
  height: 3px;
  background: #E6F0F7;
  border-radius: 6px;
  margin: 15px 0 15px 0;
  cursor: pointer;
  transition: background 0.2s;

  &:hover {
    background: #D1E7F5;
  }
`;

const ColorPickerContainer = styled.div`
  width: 387px;
  height: 273px;
  flex-shrink: 0;
  background: #fff;
  border-radius: 24px;
  margin-top: 12px;
  box-shadow: 0 2px 8px rgba(0,0,0,0.08);
  display: flex;
  align-items: center;
  justify-content: center;
`;

const WeatherWrite = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [sheetOpen, setSheetOpen] = useState(false);

  const handleSheetToggle = () => setSheetOpen(open => !open);

  return (
    <Container>
      <Card style={{ position: 'relative' }}>
        <BottomSheet showOnlyTop={!sheetOpen}>
          <BottomSheetHandle onClick={handleSheetToggle} />
          {sheetOpen && (
            <ColorPickerContainer>
              {/* 컬러 피커 UI 또는 내용 */}
            </ColorPickerContainer>
          )}
        </BottomSheet>
      </Card>
    </Container>
  );
};

export default WeatherWrite;
