import styled from 'styled-components';
import { useLocation, useNavigate } from 'react-router-dom';
import homeIcon from '../assets/home.svg';
import homeActiveIcon from '../assets/home_act.svg';
import mapIcon from '../assets/map.svg';
import myIcon from '../assets/my.svg';

const NavBarContainer = styled.div`
  width: 440px;
  height: 70px;
  flex-shrink: 0;
  background: color(display-p3 0.5654 0.78 0.8732);
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 24px 0 16px 0;
  position: fixed; 
  bottom: 0;      
  left: 50%;
  transform: translateX(-50%);
  z-index: 100;
`;

const NavItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    opacity: 0.8;
  }
`;

const NavIcon = styled.img`
  width: 40px;
  height: 40px;
  margin-bottom: 4px;
  color: ${props => props.active ? '#DAEDFF' : '#75ACC5'};
`;

const NavText = styled.div`
  margin-top: 13px;
  color: ${props => props.active ? '#DAEDFF' : '#75ACC5'};
  text-align: center;
  leading-trim: both;
  text-edge: cap;
  font-family: Pretendard;
  font-size: 12px;
  font-style: normal;
  font-weight: 500;
  line-height: 125%;
  text-transform: uppercase;
`;

export default function NavBar() {
  const location = useLocation();
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    navigate(path);
  };

  return (
    <NavBarContainer>
      <NavItem onClick={() => handleNavigation('/home')}>
        <NavIcon 
          src={homeIcon} 
          alt="home" 
          active={location.pathname === '/home'} 
        />
        <NavText active={location.pathname === '/home'}>HOME</NavText>
      </NavItem>

      {/* WEATHER MAP 메뉴는 항상 비활성화 상태로 설정 */}
      <NavItem onClick={() => handleNavigation('/map')}>
        <NavIcon 
          src={mapIcon} 
          alt="map" 
          active={false} 
        />
        <NavText active={false}>WEATHER MAP</NavText>
      </NavItem>

      {/* MY 메뉴는 항상 비활성화 상태로 설정 */}
      <NavItem onClick={() => handleNavigation('/my')}>
        <NavIcon 
          src={myIcon} 
          alt="my" 
          active={false} 
        />
        <NavText active={false}>MY</NavText>
      </NavItem>
    </NavBarContainer>
  );
}
