import React, { useEffect, useRef, useState } from 'react';
import styled from 'styled-components';
import weathersearchlogo from '../assets/weathersearchlogo.svg';
import WriteButton from '../components/WriteButton';
import markericon from '../assets/markericon.svg';
import photoinput from '../assets/photoinput.svg';
import { useNavigate } from 'react-router-dom';

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  font-family: 'Pretendard', sans-serif;
`;

const CardContainer = styled.div`
  width: 440px;
  height: 956px;
  border-radius: 5px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const MapContainer = styled.div`
  flex: 1;
  width: 100%;
  min-height: 400px;
  position: relative;
`;

const MapDiv = styled.div`
  width: 100%;
  height: 956px; 
  min-height: 400px;
`;

const SearchBarRow = styled.div`
  position: absolute;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  z-index: 10;
  display: flex;
  flex-direction: row;
  align-items: center;
  gap: 29px;
`;

const SearchBarContainer = styled.div`
`;

const SearchBar = styled.div`
  border-radius: 54px;
  background: rgba(27, 83, 35, 0.30);
  background: color(display-p3 0.1702 0.3208 0.1595 / 0.30);
  backdrop-filter: blur(10.100000381469727px);
  width: 310px;
  height: 56px;
  flex-shrink: 0;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 0 5px 0 15px;
  box-sizing: border-box;
`;

const SearchText = styled.span`
  color: #fff;
  font-family: 'Pretendard', sans-serif;
  font-size: 16px;
  font-weight: 400;
  text-align: center;
`;

const CircularButton = styled.button`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  border: none;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 5px;
  transition: all 0.2s ease;

  &:active {
    transform: scale(0.95);
  }
`;

const ButtonIcon = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 12px;
  font-weight: 300;
`;

const CustomOverlay = styled.div`
  position: absolute;
  top: 24px;
  left: 5%;
  width: 396px;
  height: 180px;
  flex-shrink: 0;
  border-radius: 25px;
  background: rgba(166, 189, 187, 0.43);
  backdrop-filter: blur(2.85px);
  z-index: 20;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: flex-start;
`;

const PhotoButton = styled.button`
  width: 102px;
  height: 102px;
  border-radius: 24px;
  background: #e0e0e0;
  border: none;
  margin-top: -45px;
  margin-left: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 18px;
  color: #333;
  cursor: pointer;
  box-shadow: 0 2px 8px 0 rgba(0,0,0,0.04);
  transition: background 0.2s;
  &:hover {
    background: #d0d0d0;
  }
`;

const WeatherMap = () => {
  const navigate = useNavigate();
  const [markerPos, setMarkerPos] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const clickListenerRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    const initializeMap = () => {
      const container = document.getElementById('map');
      if (!container) return;

      const options = {
        center: new window.kakao.maps.LatLng(37.296442644565175, 126.83532389153223),
        level: 3,
      };

      const map = new window.kakao.maps.Map(container, options);
      mapRef.current = map;

      // 기존 리스너 제거
      if (clickListenerRef.current) {
        window.kakao.maps.event.removeListener(map, 'click', clickListenerRef.current);
      }

      // 새 리스너 등록
      const clickListener = function(mouseEvent) {
        const latlng = mouseEvent.latLng;
        const newLat = latlng.getLat();
        const newLng = latlng.getLng();
        // 이미 같은 위치에 마커가 있으면 해제
        if (markerPos && Math.abs(markerPos.lat - newLat) < 1e-6 && Math.abs(markerPos.lng - newLng) < 1e-6) {
          setMarkerPos(null);
        } else {
          setMarkerPos({ lat: newLat, lng: newLng });
        }
      };
      window.kakao.maps.event.addListener(map, 'click', clickListener);
      clickListenerRef.current = clickListener;
    };

    const loadKakaoMapScript = () => {
      if (window.kakao && window.kakao.maps) {
        initializeMap();
        return;
      }

      if (document.getElementById('kakao-map-script')) {
        return;
      }

      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=236caf5234baf8221c0e94c739ba6412&autoload=false`;
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(() => {
          initializeMap();
        });
      };

      script.onerror = (error) => {
        console.error('Kakao map script load error:', error);
      };

      document.head.appendChild(script);
    };

    const timer = setTimeout(loadKakaoMapScript, 300);
    return () => clearTimeout(timer);
  }, []);

  // 마커 렌더링
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps) return;
    if (!markerPos) {
      if (markerRef.current) {
        markerRef.current.setMap(null);
        markerRef.current = null;
      }
      return;
    }
    // 마커가 이미 있으면 위치만 이동
    if (markerRef.current) {
      markerRef.current.setPosition(new window.kakao.maps.LatLng(markerPos.lat, markerPos.lng));
      markerRef.current.setMap(mapRef.current);
    } else {
      markerRef.current = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(markerPos.lat, markerPos.lng),
        image: new window.kakao.maps.MarkerImage(
          markericon,
          new window.kakao.maps.Size(44, 44),
          { offset: new window.kakao.maps.Point(22, 44) }
        )
      });
      markerRef.current.setMap(mapRef.current);
    }
  }, [markerPos]);

  const handleSearchClick = () => {
    console.log('Search button clicked from WeatherMap');
  };

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <MapDiv id="map" />
          {markerPos && (
            <CustomOverlay>
              <PhotoButton onClick={() => fileInputRef.current && fileInputRef.current.click()}>
                <img src={photoinput} alt="photoinput" style={{width:102,height:102}} />
              </PhotoButton>
              <input
                type="file"
                accept="image/*"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={e => {
                  if (e.target.files && e.target.files[0]) {
                    setSelectedFile(e.target.files[0]);
                  }
                }}
              />
            </CustomOverlay>
          )}
        </MapContainer>
        <SearchBarRow>
            <SearchBarContainer>
                <SearchBar>
                <SearchText>장소를 검색해 보세요.</SearchText>
                <CircularButton onClick={handleSearchClick}>
                    <ButtonIcon><img src={weathersearchlogo} alt="weatersearch" style={{width:44,height:44,marginRight:5}} /></ButtonIcon>
                </CircularButton>
                </SearchBar>
            </SearchBarContainer>
            <WriteButton onClick={() => navigate('/weather-write')} />
        </SearchBarRow>
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
