import React, { useState } from 'react';
import styled from 'styled-components';

const SearchContainer = styled.div`
  position: relative;
  margin-top: auto;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 12px 16px;
  padding-right: 80px;
  border: 1px solid #e0e0e0;
  border-radius: 24px;
  font-size: 14px;
  outline: none;
  background: #f8f9fa;

  &:focus {
    border-color: #5a9fd4;
    background: white;
  }

  &::placeholder {
    color: #999;
  }
`;

const SearchActions = styled.div`
  position: absolute;
  right: 8px;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  gap: 8px;
  align-items: center;
`;

const SearchButton = styled.button`
  width: 32px;
  height: 32px;
  border: none;
  border-radius: 50%;
  background: #5a9fd4;
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background: #4a8bc2;
  }
`;

const SubmitButton = styled.button`
  padding: 6px 12px;
  border: none;
  border-radius: 16px;
  background: #5a9fd4;
  color: white;
  font-size: 12px;
  cursor: pointer;

  &:hover {
    background: #4a8bc2;
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
`;

const SearchBar = ({ onFocus, onSubmit, selectedCount }) => {
  const [searchValue, setSearchValue] = useState('');

  const handleFocus = () => {
    if (onFocus) onFocus();
  };

  const handleSubmit = () => {
    if (searchValue.trim()) {
      console.log('Í≤ÄÏÉâ:', searchValue);
      if (onSubmit) onSubmit(searchValue);
    }
  };

  const isDisabled = selectedCount < 1 || selectedCount > 10;

  return (
    <SearchContainer>
      <SearchInput
        type="text"
        placeholder="ÎÇ†Ïî®Î•º ÏûÖÎ†•Ìï¥ Î≥¥ÏÑ∏Ïöî."
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
        onFocus={handleFocus}
      />
      <SearchActions>
        <SearchButton type="button">üîç</SearchButton>
        <SubmitButton onClick={handleSubmit} disabled={isDisabled}>
          Îã§Ïùå
        </SubmitButton>
      </SearchActions>
    </SearchContainer>
  );
};

export default SearchBar;
