import React, { useState } from 'react';
import styled from 'styled-components';

const SearchContainer = styled.div`
  position: relative;
  margin-top: auto;
`;

const SearchInput = styled.input`
  width: 236px;
  height: 40px;
  flex-shrink: 0;
  padding: 12px 16px;
  padding-right: 100px;
  border-radius: 54px;
  border: 0.7px solid #CCC;
  border: 0.7px solid color(display-p3 0.7987 0.7987 0.7987);
  background: rgba(131, 131, 131, 0.30);
  background: color(display-p3 0.5149 0.5149 0.5149 / 0.30);
  backdrop-filter: blur(12.699999809265137px);
  font-size: 20px;
  outline: none;
  color: white;

  &:focus {
    border-color: #5a9fd4;
  }

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }
`;

const SearchActions = styled.div`
  position: absolute;
  right: 30px;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  gap: 8px;
  align-items: center;
`;

const SearchButton = styled.button`
  width: 30px;
  height: 30px;
  border: none;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.2);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 12px;
  backdrop-filter: blur(12.7px);

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }
`;

const SubmitButton = styled.button`
  padding: 8px 10px;
  height: 40px;
  border: none;
  border-radius: 16px;
  background: rgba(255, 255, 255, 0.2);
  color: #1D1D1D;
  font-size: 20px;
  font-weight: 500;
  cursor: pointer;
  backdrop-filter: blur(12.7px);
  white-space: nowrap;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
  }

  &:disabled {
    background: rgba(255, 255, 255, 0.1);
    color: rgba(29, 29, 29, 0.5);
    cursor: not-allowed;
  }
`;

const SearchBar = ({ onFocus, onSubmit, selectedCount }) => {
  const [searchValue, setSearchValue] = useState('');

  const handleFocus = () => {
    if (onFocus) onFocus();
  };

  const handleSubmit = () => {
    if (onSubmit) {
      onSubmit(searchValue.trim());
    }
  };

  const isDisabled = selectedCount < 1 || selectedCount > 10;

  return (
    <SearchContainer>
      <SearchInput
        type="text"
        placeholder="ÎÇ†Ïî®Î•º ÏûÖÎ†•Ìï¥ Î≥¥ÏÑ∏Ïöî."
        value={searchValue}
        onChange={(e) => setSearchValue(e.target.value)}
        onFocus={handleFocus}
      />
      <SearchActions>
        <SearchButton type="button">üîç</SearchButton>
        <SubmitButton onClick={handleSubmit} disabled={isDisabled}>
          Îã§Ïùå
        </SubmitButton>
      </SearchActions>
    </SearchContainer>
  );
};

export default SearchBar;
