import React, { useState } from 'react';
import { ChevronLeft } from 'lucide-react';
import styled from 'styled-components';
import ProgressBar from './ProgressBar.jsx';
import ImageGallery from './ImageGallery.jsx';

const Container = styled.div`
  background-color: #2d2d2d;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
`;

const CardWrapper = styled.div`
  background: white;
  border-radius: 12px;
  width: 400px;
  height: 700px;
  display: flex;
  flex-direction: column;
  overflow: hidden;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
`;

const CardHeader = styled.div`
  padding: 20px;
  border-bottom: 1px solid #f0f0f0;
`;

const CardTitle = styled.h3`
  margin: 0 0 16px 0;
  font-size: 18px;
  font-weight: 600;
  color: #333;
`;

const CardContent = styled.div`
  flex: 1;
  padding: 20px;
  display: flex;
  flex-direction: column;
`;

const ContentText = styled.p`
  font-size: 16px;
  color: #666;
  line-height: 1.6;
  margin: 0 0 24px 0;
`;

const GallerySection = styled.div`
  flex: 1;
  margin-bottom: 20px;
`;

const BottomButton = styled.button`
  width: 100%;
  padding: 16px;
  background: #5a9fd4;
  color: white;
  border: none;
  border-radius: 24px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  margin-top: auto;

  &:hover {
    background: #4a8bc2;
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
  }
`;

const OnboardingFlow = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [selectedCount, setSelectedCount] = useState(0);

  const steps = [
    {
      title: "거의 다 왔어요! 영이님이 좋아하는 날씨를 골라주세요.",
      progress: 80,
      subtitle: "최대 10개까지 등록할 수 있어요."
    },
    {
      title: "거의 다 왔어요! 영이님이 좋아하는 계절을 골라주세요.",
      progress: 90,
      subtitle: "가장 좋아하는 계절을 선택해주세요."
    }
  ];

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handleBack = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSelectionChange = (count) => {
    setSelectedCount(count);
  };

  const currentStepData = steps[currentStep];
  const isLastStep = currentStep === steps.length - 1;
  const isNextDisabled = selectedCount < 1 || selectedCount > 10;

  return (
    <Container>
      <CardWrapper>
        <CardHeader>
          <ProgressBar percentage={currentStepData.progress} text={`온보딩 ${currentStepData.progress}%`} />
          <CardTitle>{currentStepData.title}</CardTitle>
        </CardHeader>

        <CardContent>
          <ContentText>
            {currentStepData.subtitle}
          </ContentText>

          <GallerySection>
            <ImageGallery onSelectionChange={handleSelectionChange} />
          </GallerySection>

          <BottomButton 
            onClick={handleNext}
            disabled={isNextDisabled}
          >
            {isLastStep ? '저장하기' : '다음'}
          </BottomButton>
        </CardContent>
      </CardWrapper>
    </Container>
  );
};

export default OnboardingFlow;
