const WeatherMap = () => {
  const navigate = useNavigate();
  const [currentMarker, setCurrentMarker] = useState(null); // 현재 생성된 마커
  const [selectedFile, setSelectedFile] = useState(null);
  const mapRef = useRef(null);
  const fileInputRef = useRef(null);
  const [placeText, setPlaceText] = useState('');
  const [placeKeyword, setPlaceKeyword] = useState('');
  const [overlayVisible, setOverlayVisible] = useState(false);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [fixedMarkers, setFixedMarkers] = useState([]); // 고정된 마커들
  const [tempPlaceText, setTempPlaceText] = useState('');
  const [tempPlaceKeyword, setTempPlaceKeyword] = useState('');

  // 카카오 맵 초기화
  useEffect(() => {
    const initializeMap = () => {
      const container = document.getElementById('map');
      if (!container) return;

      const options = {
        center: new window.kakao.maps.LatLng(37.296442644565175, 126.83532389153223),
        level: 3,
      };
      const map = new window.kakao.maps.Map(container, options);
      mapRef.current = map;
      setMapLoaded(true);

      const clickListener = function (mouseEvent) {
        const latlng = mouseEvent.latLng;
        const lat = latlng.getLat();
        const lng = latlng.getLng();

        console.log('Map clicked at:', lat, lng);

        if (currentMarker) {
          const currentPos = currentMarker.getPosition();
          const distance = Math.sqrt(
            Math.pow(currentPos.getLat() - lat, 2) +
            Math.pow(currentPos.getLng() - lng, 2)
          );
          if (distance < 0.0001) {
            currentMarker.setMap(null);
            setCurrentMarker(null);
            setOverlayVisible(false);
            return;
          }
          currentMarker.setMap(null);
        }

        const marker = new window.kakao.maps.Marker({
          position: new window.kakao.maps.LatLng(lat, lng),
          image: new window.kakao.maps.MarkerImage(
            markericon,
            new window.kakao.maps.Size(44, 44),
            { offset: new window.kakao.maps.Point(22, 44) }
          )
        });

        marker.setMap(map);
        console.log("Marker set on map");
        setCurrentMarker(marker);
        setOverlayVisible(true);
        setTempPlaceText('');
        setTempPlaceKeyword('');
      };

      window.kakao.maps.event.addListener(map, 'click', clickListener);
    };

    if (window.kakao && window.kakao.maps) {
      window.kakao.maps.load(() => {
        initializeMap();
      });
    } else {
      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=236caf5234baf8221c0e94c739ba6412&autoload=false`;
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(() => {
          initializeMap();
        });
      };
      script.onerror = (error) => {
        console.error('Kakao map script load error:', error);
      };

      document.head.appendChild(script);
    }
  }, []);


  // 지도 클릭 이벤트 - 마커 생성/해제
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps) return;

    const map = mapRef.current;

    const clickListener = function(mouseEvent) {
      const latlng = mouseEvent.latLng;
      const lat = latlng.getLat();
      const lng = latlng.getLng();

      console.log('Map clicked at:', lat, lng);

      // 현재 마커와 같은 위치를 클릭했는지 확인
      if (currentMarker) {
        const currentPos = currentMarker.getPosition();
        const distance = Math.sqrt(
          Math.pow(currentPos.getLat() - lat, 2) + 
          Math.pow(currentPos.getLng() - lng, 2)
        );

        // 같은 위치 클릭 시 마커 해제 (거리가 매우 작을 때)
        if (distance < 0.0001) {
          currentMarker.setMap(null);
          setCurrentMarker(null);
          setOverlayVisible(false);
          return;
        }

        // 다른 위치 클릭 시 기존 마커 제거
        currentMarker.setMap(null);
      }

      // 새 마커 생성
      const marker = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(lat, lng),
        image: new window.kakao.maps.MarkerImage(
          markericon,
          new window.kakao.maps.Size(44, 44),
          { offset: new window.kakao.maps.Point(22, 44) }
        )
      });

      // 마커를 지도에 표시
      marker.setMap(map);
      setCurrentMarker(marker);

      // 오버레이 표시
      setOverlayVisible(true);
      setTempPlaceText('');
      setTempPlaceKeyword('');
    };

    window.kakao.maps.event.addListener(map, 'click', clickListener);

    return () => {
      window.kakao.maps.event.removeListener(map, 'click', clickListener);
    };
  }, [currentMarker]);

  // 마커 클릭 이벤트 별도 관리
  useEffect(() => {
    if (!currentMarker || !window.kakao || !window.kakao.maps) return;

    const markerClickListener = function() {
      console.log('Marker clicked');
      if (!overlayVisible) {
        setOverlayVisible(true);
        setTempPlaceText('');
        setTempPlaceKeyword('');
      }
    };

    window.kakao.maps.event.addListener(currentMarker, 'click', markerClickListener);

    return () => {
      if (currentMarker) {
        window.kakao.maps.event.removeListener(currentMarker, 'click', markerClickListener);
      }
    };
  }, [currentMarker, overlayVisible]);

  // 고정된 마커들 렌더링
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps || !mapLoaded) return;

    // 고정된 마커들 생성
    fixedMarkers.forEach(markerData => {
      const marker = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(markerData.lat, markerData.lng),
        image: new window.kakao.maps.MarkerImage(
          markericon,
          new window.kakao.maps.Size(44, 44),
          { offset: new window.kakao.maps.Point(22, 44) }
        )
      });
      marker.setMap(mapRef.current);

      // 고정된 마커 클릭 이벤트
      const fixedMarkerClickListener = function() {
        setOverlayVisible(true);
        setTempPlaceText(markerData.text);
        setTempPlaceKeyword(markerData.keyword);
      };

      window.kakao.maps.event.addListener(marker, 'click', fixedMarkerClickListener);
    });
  }, [fixedMarkers, mapLoaded]);

  // 마커 고정 함수
  const fixMarker = () => {
    if (!currentMarker || (!tempPlaceText.trim() && !tempPlaceKeyword.trim())) return;

    const position = currentMarker.getPosition();
    const newFixedMarker = {
      lat: position.getLat(),
      lng: position.getLng(),
      text: tempPlaceText.trim(),
      keyword: tempPlaceKeyword.trim(),
      id: Date.now()
    };

    // 고정된 마커 목록에 추가
    setFixedMarkers(prev => [...prev, newFixedMarker]);

    // 현재 마커 제거 (고정된 마커로 대체됨)
    setCurrentMarker(null);

    // 오버레이 숨기기
    setOverlayVisible(false);

    console.log('Marker fixed:', newFixedMarker);
  };

  const handleSearchClick = () => {
    console.log('Search button clicked from WeatherMap');
  };

  // 오버레이 닫기
  const closeOverlay = () => {
    setOverlayVisible(false);
    // 현재 마커가 있고 아직 고정되지 않았다면 제거
    if (currentMarker) {
      currentMarker.setMap(null);
      setCurrentMarker(null);
    }
  };

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <MapDiv id="map" />
          {overlayVisible && (
            <CustomOverlay>
              <PhotoButton onClick={() => fileInputRef.current && fileInputRef.current.click()}>
                <img src={photoinput} alt="photoinput" style={{width:102,height:102}} />
              </PhotoButton>
              <div style={{ width: 250, display: 'flex', flexDirection: 'column', alignItems: 'flex-start', justifyContent: 'flex-start'}}>
                <OverlayTextInput
                  value={tempPlaceText}
                  onChange={(e) => setTempPlaceText(e.target.value)}
                  placeholder="장소 이름을 입력하세요"
                />
                <KeywordInput
                  type="text"
                  value={tempPlaceKeyword}
                  onChange={(e) => setTempPlaceKeyword(e.target.value)}
                  placeholder="키워드를 입력하세요"
                />
                <SaveButton onClick={fixMarker}>
                  저장
                </SaveButton>
              </div>
              <input
                type="file"
                accept="image/*"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={e => {
                  if (e.target.files && e.target.files[0]) {
                    setSelectedFile(e.target.files[0]);
                  }
                }}
              />
              <OverlayActionButton onClick={() => navigate('/community')}>
                <img src={custombtn} alt="custombtn" style={{width:10,height:20}} />
              </OverlayActionButton>
              <CloseButton onClick={closeOverlay}>
                ×
              </CloseButton>
            </CustomOverlay>
          )}
        </MapContainer>
        <SearchBarRow>
            <SearchBarContainer>
                <SearchBar>
                <SearchText>장소를 검색해 보세요.</SearchText>
                <CircularButton onClick={handleSearchClick}>
                    <ButtonIcon><img src={weathersearchlogo} alt="weatersearch" style={{width:44,height:44,marginRight:5}} /></ButtonIcon>
                </CircularButton>
                </SearchBar>
            </SearchBarContainer>
            <WriteButton onClick={() => navigate('/weather-write')} />
        </SearchBarRow>
        <Navbar />
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
