import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';
import paintIcon from '../assets/paint.svg';
import musicIcon from '../assets/music.svg';
import pictureIcon from '../assets/picture.svg';
import bgImg from '../assets/background.png';

const HomeContainer = styled.div`
  width: 440px;
  height: 956px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url(${bgImg});
  background-size: cover;
  background-position: center;
`;

const Overlay = styled.div`
  width: 440px;
  height: 956px;
  z-index: 200;
  backdrop-filter: blur(8px);
  background: transparent;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const CenterBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100vw;
  height: 100vh;
`;

const StyledInput = styled.input`
  background: none;
  border: none;
  border-bottom: 2px solid #fff;
  color: #fff;
  font-size: 22px;
  text-align: center;
  outline: none;
  width: 90vw;
  max-width: 340px;
  margin: 0 auto 32px auto;
  padding: 16px 0;
  font-family: Pretendard;
  &::placeholder {
    color: #fff;
    opacity: 0.9;
    font-size: 22px;
    text-align: center;
  }
`;

const ButtonRow = styled.div`
  display: flex;
  gap: 32px;
  justify-content: center;
  margin-top: 32px;
`;

const IconButton = styled.button`
  background: transparent;
  border: none;
  width: 56px;
  height: 56px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: background 0.2s;
  &:hover {
    background: rgba(39, 39, 39, 0.13);
    border-radius: 50%;
  }
`;

export default function Write() {
  const inputRef = useRef(null);
  useEffect(() => {
    if (inputRef.current) inputRef.current.focus();
  }, []);
  return (
    <HomeContainer>
    <Overlay>
      <CenterBox>
        <StyledInput
          ref={inputRef}
          autoFocus
          placeholder="오늘 하루, 날씨 이야기를 기록해주세요."
        />
        <ButtonRow>
          <IconButton><img src={paintIcon} alt="그림" /></IconButton>
          <IconButton><img src={musicIcon} alt="음악" /></IconButton>
          <IconButton><img src={pictureIcon} alt="사진" /></IconButton>
        </ButtonRow>
      </CenterBox>
    </Overlay>
    </HomeContainer>
  );
}
