import { useState } from 'react';

export default function BottomInputBar({ onSubmit }) {
  const [keyword, setKeyword] = useState('');

  const handleSubmit = () => {
    if (keyword.trim() !== '') {
      onSubmit(keyword.trim());
      setKeyword('');
    }
  };

  return (
    <div className="flex items-center gap-2 w-full px-4 py-3 border-t bg-white fixed bottom-0 left-0">
      <input
        type="text"
        placeholder="날씨를 입력해 주세요 (예: 맑음)"
        value={keyword}
        onChange={(e) => setKeyword(e.target.value)}
        className="flex-1 border border-gray-300 rounded-full px-4 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
      />
      <button
        onClick={handleSubmit}
        className="bg-blue-500 text-white rounded-full px-4 py-2 text-sm hover:bg-blue-600"
      >
        다음
      </button>
    </div>
  );
}
