import React from 'react';
import styled from 'styled-components';
import writebackground from '../assets/writebackground.png';
import WriteButton from '../components/WriteButton';
import { useNavigate, useLocation } from 'react-router-dom';

const Container = styled.div`
  width: 440px;
  height: 956px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url(${writebackground});
  background-size: cover;
  background-position: center;
`;

const Card = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const BottomSheet = styled.div`
  width: 440px;
  height: ${props => props.showOnlyTop ? '30px' : '413px'};
  flex-shrink: 0;
  border-radius: 45px 45px 0px 0px;
  background: rgba(155, 155, 155, 0.39);
  background: color(display-p3 0.6095 0.6095 0.6095 / 0.39);
  box-shadow: 0px -1px 4px 0px rgba(0, 0, 0, 0.25);
  box-shadow: 0px -1px 4px 0px color(display-p3 0 0 0 / 0.25);
  backdrop-filter: blur(15.6px);
  position: absolute;
  left: 0;
  bottom: 0;
  overflow: hidden;
  transition: height 0.3s;
`;

const BottomSheetHandle = styled.div`
  width: 72px;
  height: 3px;
  background: #E6F0F7;
  border-radius: 6px;
  margin: 18px 0 18px 0;
  cursor: pointer;
  transition: background 0.2s;

  &:hover {
    background: #D1E7F5;
  }
`;

const WeatherWrite = () => {
  const navigate = useNavigate();
  const location = useLocation();

  return (
    <Container>
      <Card style={{ position: 'relative' }}>
        <BottomSheet showOnlyTop={location.pathname === '/weather-write'} />
      </Card>
    </Container>
  );
};

export default WeatherWrite;
