import React, { useState } from 'react';
import { ChevronLeft, ChevronDown } from 'lucide-react';

const OnboardingFlow = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [selectedOptions, setSelectedOptions] = useState({
    step1: { category: '', preference: '' },
    step2: { category: '', preference: '' },
    step3: { category: '', preference: '' },
    step4: { category: '', preference: '' }
  });
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [selectedWeathers, setSelectedWeathers] = useState([]);
  const [customWeather, setCustomWeather] = useState('');

  const seasons = ['봄', '여름', '가을', '겨울'];

  const steps = [
    {
      title: "거의 다 왔어요! 영이님이 좋아하는 날씨를 알려주세요.",
      progress: 80,
      options: [
        { category: "계절", preference: "을(를) 가장 좋아하고", placeholder: "구름한점 없이 맑은", answer: "한 날씨를 좋아해요.", hasDropdown: true }
      ]
    },
    {
      title: "거의 다 왔어요! 영이님의 싶어하는 날씨를 알려주세요.",
      progress: 90,
      options: [
        { category: "가을", preference: "을(를) 가장 좋아하고,", placeholder: "흐린 날이 차분", answer: "한 날씨를 좋아해요.", hasDropdown: false },
        { category: "겨울", preference: "을(를) 가장 싶어하고,", placeholder: "맑고 쌀쌀한", answer: "한 날씨를 싶어해요.", hasDropdown: false }
      ]
    }
  ];

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handleBack = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSeasonSelect = (season) => {
    const stepKey = `step${currentStep + 1}`;
    setSelectedOptions(prev => ({
      ...prev,
      [stepKey]: {
        ...prev[stepKey],
        category: season
      }
    }));
    setDropdownOpen(false);
  };

  const handleWeatherSelect = (weather) => {
    if (!selectedWeathers.find(w => w.id === weather.id)) {
      setSelectedWeathers([...selectedWeathers, weather]);
    }
  };

  const handleWeatherRemove = (weatherId) => {
    setSelectedWeathers(selectedWeathers.filter(w => w.id !== weatherId));
  };

  const currentStepData = steps[currentStep];
  const isLastStep = currentStep === steps.length - 1;

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white rounded-3xl shadow-lg overflow-hidden">
        {/* Header */}
        <div className="bg-gray-700 text-white p-4 relative">
          <div className="text-center text-sm font-medium">온보딩 {currentStepData.progress}%</div>
          <div className="w-full bg-gray-600 rounded-full h-1 mt-2">
            <div 
              className="bg-blue-400 h-1 rounded-full transition-all duration-300"
              style={{ width: `${currentStepData.progress}%` }}
            ></div>
          </div>
          <button 
            onClick={handleBack}
            className="absolute left-4 top-1/2 transform -translate-y-1/2 text-white"
          >
            <ChevronLeft size={20} />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 pb-24" onClick={() => setDropdownOpen(false)}>
          <h1 className="text-lg font-bold text-gray-900 mb-2 leading-relaxed">
            {currentStepData.title}
          </h1>

          {currentStepData.subtitle && (
            <p className="text-sm text-gray-500 mb-8">{currentStepData.subtitle}</p>
          )}

          {currentStepData.type === 'weather_selection' ? (
            <div className="space-y-6">
              {/* Weather Grid */}
              <div className="grid grid-cols-2 gap-4">
                {currentStepData.weatherOptions.map((weather) => (
                  <div
                    key={weather.id}
                    onClick={() => handleWeatherSelect(weather)}
                    className={`relative rounded-2xl p-6 cursor-pointer transition-all ${
                      weather.id === 3 ? 'row-span-2 h-full' : 'h-32'
                    } bg-gradient-to-br ${weather.color} hover:scale-105`}
                  >
                    <div className="absolute bottom-4 left-4">
                      <span className="bg-black bg-opacity-30 text-white px-3 py-1 rounded-full text-sm font-medium">
                        {weather.name}
                      </span>
                    </div>
                  </div>
                ))}
              </div>

              {/* Selected Weather Tags */}
              {selectedWeathers.length > 0 && (
                <div className="flex flex-wrap gap-2 mt-4">
                  {selectedWeathers.map((weather) => (
                    <div
                      key={weather.id}
                      className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm flex items-center gap-2"
                    >
                      {weather.name}
                      <button
                        onClick={() => handleWeatherRemove(weather.id)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        ×
                      </button>
                    </div>
                  ))}
                </div>
              )}

              {/* Input Field */}
              <div className="relative">
                <div className="flex items-center bg-gray-50 rounded-full px-4 py-3">
                  <input
                    type="text"
                    value={customWeather}
                    onChange={(e) => setCustomWeather(e.target.value)}
                    placeholder={currentStepData.inputPlaceholder}
                    className="flex-1 bg-transparent text-sm text-gray-700 placeholder-gray-400 outline-none"
                  />
                  <button className="ml-2 w-10 h-10 bg-blue-500 rounded-full flex items-center justify-center">
                    <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                    </svg>
                  </button>
                </div>
              </div>
            </div>
          ) : (
            <div className="space-y-6" onClick={(e) => e.stopPropagation()}>
              {currentStepData.options.map((option, optionIndex) => (
                <div key={optionIndex} className="space-y-4">
                  {/* Category Dropdown */}
                  <div className="relative">
                    {option.hasDropdown ? (
                      <div className="relative">
                        <button 
                          onClick={() => setDropdownOpen(!dropdownOpen)}
                          className="w-auto bg-gray-100 rounded-full px-4 py-2 text-sm text-gray-700 flex items-center gap-2 hover:bg-gray-200 transition-colors"
                        >
                          <span>{selectedOptions[`step${currentStep + 1}`]?.category || option.category}</span>
                          <ChevronDown size={16} className={`transition-transform ${dropdownOpen ? 'rotate-180' : ''}`} />
                        </button>

                        {dropdownOpen && (
                          <div className="absolute top-full left-0 mt-1 bg-white border border-gray-200 rounded-lg shadow-lg z-10 min-w-[120px]">
                            {seasons.map((season) => (
                              <button
                                key={season}
                                onClick={() => handleSeasonSelect(season)}
                                className="w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50 first:rounded-t-lg last:rounded-b-lg"
                              >
                                {season}
                              </button>
                            ))}
                          </div>
                        )}
                      </div>
                    ) : (
                      <button className="w-auto bg-gray-100 rounded-full px-4 py-2 text-sm text-gray-700 flex items-center gap-2">
                        <span>{option.category}</span>
                        <ChevronDown size={16} />
                      </button>
                    )}
                  </div>

                  {/* Preference Text */}
                  <div className="flex items-center flex-wrap gap-1 text-sm text-gray-700">
                    <span>{option.preference}</span>
                  </div>

                  {/* Input Field */}
                  <div className="space-y-2">
                    <div className="text-xs text-gray-400">{option.placeholder}</div>
                    <div className="text-sm text-gray-900">{option.answer}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Bottom Buttons */}
        <div className="absolute bottom-0 left-0 right-0 p-6 bg-white">
          <div className="flex gap-3">
            {isLastStep ? (
              <>
                <button className="flex-1 bg-blue-400 text-white py-4 rounded-full font-medium text-center">
                  저장하기
                </button>
                <button className="px-6 py-4 text-gray-600 font-medium">
                  취소
                </button>
              </>
            ) : (
              <button 
                onClick={handleNext}
                className="w-full bg-blue-400 text-white py-4 rounded-full font-medium"
              >
                다음
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default OnboardingFlow;
