import styled from 'styled-components';
import { useLocation, useNavigate } from 'react-router-dom';
import { ReactComponent as HomeIcon } from '../assets/home.svg';
import { ReactComponent as MapIcon } from '../assets/map.svg';
import { ReactComponent as MyIcon } from '../assets/my.svg';

const NavBarContainer = styled.div`
  width: 440px;
  height: 96px;
  flex-shrink: 0;
  background: #99cce3;
  display: flex;
  justify-content: space-around;
  align-items: center;
  padding: 24px 0 16px 0;
`;

const NavItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    opacity: 0.8;
  }
`;

const NavIcon = styled.div`
  width: 40px;
  height: 40px;
  margin-bottom: 4px;

  & svg {
    width: 100%;
    height: 100%;
    fill: ${props => props.active ? '#D6EEFF' : '#75ACC5'};
    transition: fill 0.2s ease;
  }
`;

const NavText = styled.div`
  color: ${props => props.active ? '#D6EEFF' : '#75ACC5'};
  text-align: center;
  leading-trim: both;
  text-edge: cap;
  font-family: Pretendard;
  font-size: 12px;
  font-style: normal;
  font-weight: 500;
  line-height: 125%;
  text-transform: uppercase;
  transition: color 0.2s ease;
`;

export default function NavBar() {
  const location = useLocation();
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    navigate(path);
  };

  return (
    <NavBarContainer>
      <NavItem onClick={() => handleNavigation('/home')}>
        <NavIcon active={location.pathname === '/home'}>
          <HomeIcon />
        </NavIcon>
        <NavText active={location.pathname === '/home'}>HOME</NavText>
      </NavItem>

      <NavItem onClick={() => handleNavigation('/map')}>
        <NavIcon active={location.pathname === '/map'}>
          <MapIcon />
        </NavIcon>
        <NavText active={location.pathname === '/map'}>WEATHER MAP</NavText>
      </NavItem>

      <NavItem onClick={() => handleNavigation('/my')}>
        <NavIcon active={location.pathname === '/my'}>
          <MyIcon />
        </NavIcon>
        <NavText active={location.pathname === '/my'}>MY</NavText>
      </NavItem>
    </NavBarContainer>
  );
}
