const WeatherMap = () => {
  const navigate = useNavigate();
  const [markerPos, setMarkerPos] = useState(() => {
    const lat = localStorage.getItem('placeLat');
    const lng = localStorage.getItem('placeLng');
    return lat && lng ? { lat: parseFloat(lat), lng: parseFloat(lng) } : null;
  });
  const [isMarkerLocked, setIsMarkerLocked] = useState(() => {
    return !!(localStorage.getItem('placeText') && localStorage.getItem('placeKeyword'));
  });
  const markerPosRef = useRef(markerPos);
  const [selectedFile, setSelectedFile] = useState(null);
  const mapRef = useRef(null);
  const markerRef = useRef(null);
  const fileInputRef = useRef(null);
  const [placeText, setPlaceText] = useState('');
  const [placeKeyword, setPlaceKeyword] = useState('');
  const [overlayVisible, setOverlayVisible] = useState(false);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [fixedMarkers, setFixedMarkers] = useState([]); // 고정된 마커들 관리
  const [tempPlaceText, setTempPlaceText] = useState(''); // 임시 텍스트 입력
  const [tempPlaceKeyword, setTempPlaceKeyword] = useState(''); // 임시 키워드 입력

  // markerPosRef 업데이트
  useEffect(() => { 
    markerPosRef.current = markerPos; 
  }, [markerPos]);

  // 카카오 맵 초기화
  useEffect(() => {
    const initializeMap = () => {
      const container = document.getElementById('map');
      if (!container) return;

      const options = {
        center: new window.kakao.maps.LatLng(37.296442644565175, 126.83532389153223),
        level: 3,
      };
      const map = new window.kakao.maps.Map(container, options);
      mapRef.current = map;
      setMapLoaded(true);
    };

    if (window.kakao && window.kakao.maps) {
      initializeMap();
    } else {
      if (document.getElementById('kakao-map-script')) {
        return;
      }

      const script = document.createElement('script');
      script.id = 'kakao-map-script';
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=236caf5234baf8221c0e94c739ba6412&autoload=false`;
      script.async = true;

      script.onload = () => {
        window.kakao.maps.load(() => {
          initializeMap();
        });
      };

      script.onerror = (error) => {
        console.error('Kakao map script load error:', error);
      };

      document.head.appendChild(script);
    }
  }, []);

  // 마커 고정 상태를 localStorage 값이 바뀔 때마다 갱신
  useEffect(() => {
    setIsMarkerLocked(!!(localStorage.getItem('placeText') && localStorage.getItem('placeKeyword')));
  }, [overlayVisible]); // overlayVisible이 바뀔 때마다 체크

  // 지도 클릭: 마커가 고정되지 않은 경우에만 위치 변경
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps) return;
    const map = mapRef.current;
    const clickListener = function(mouseEvent) {
      // 마커가 고정된 상태든 아니든, 지도 클릭 시 기존 정보 모두 초기화
      localStorage.removeItem('placeText');
      localStorage.removeItem('placeKeyword');
      localStorage.removeItem('placeLat');
      localStorage.removeItem('placeLng');
      setOverlayVisible(true);

      // 새 마커 생성
      const latlng = mouseEvent.latLng;
      setMarkerPos({ lat: latlng.getLat(), lng: latlng.getLng() });
      localStorage.setItem('placeLat', latlng.getLat());
      localStorage.setItem('placeLng', latlng.getLng());
      setIsMarkerLocked(false); // 새 마커는 고정 X
    };
    window.kakao.maps.event.addListener(map, 'click', clickListener);
    return () => {
      window.kakao.maps.event.removeListener(map, 'click', clickListener);
    };
  }, []);

  // 고정된 마커들 렌더링
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps || !mapLoaded) return;

    // 고정된 마커들 생성
    fixedMarkers.forEach(markerData => {
      const marker = new window.kakao.maps.Marker({
        position: new window.kakao.maps.LatLng(markerData.lat, markerData.lng),
        image: new window.kakao.maps.MarkerImage(
          markericon,
          new window.kakao.maps.Size(44, 44),
          { offset: new window.kakao.maps.Point(22, 44) }
        )
      });
      marker.setMap(mapRef.current);
    });
  }, [fixedMarkers, mapLoaded]);

  // 마커 고정 함수
  const fixMarker = () => {
    if (!markerPos || (!tempPlaceText.trim() && !tempPlaceKeyword.trim())) return;

    const newFixedMarker = {
      lat: markerPos.lat,
      lng: markerPos.lng,
      text: tempPlaceText.trim(),
      keyword: tempPlaceKeyword.trim(),
      id: Date.now()
    };

    // 이미 같은 위치에 고정된 마커가 있는지 확인
    const existingIndex = fixedMarkers.findIndex(marker => 
      Math.abs(marker.lat - markerPos.lat) < 1e-6 && 
      Math.abs(marker.lng - markerPos.lng) < 1e-6
    );

    if (existingIndex >= 0) {
      // 기존 마커 업데이트
      const updatedMarkers = [...fixedMarkers];
      updatedMarkers[existingIndex] = newFixedMarker;
      setFixedMarkers(updatedMarkers);
    } else {
      // 새 마커 추가
      setFixedMarkers(prev => [...prev, newFixedMarker]);
    }

    // 현재 상태 업데이트
    setPlaceText(tempPlaceText.trim());
    setPlaceKeyword(tempPlaceKeyword.trim());

    // 오버레이 숨기기
    setOverlayVisible(false);
    setMarkerPos(null);

    console.log('Marker fixed:', newFixedMarker);
  };

  // 임시 마커 생성 및 관리
  useEffect(() => {
    if (!mapRef.current || !window.kakao || !window.kakao.maps || !mapLoaded) return;

    // 기존 임시 마커 제거
    if (markerRef.current) {
      markerRef.current.setMap(null);
      markerRef.current = null;
    }

    // 마커 위치가 없으면 리턴
    if (!markerPos) {
      return;
    }

    console.log('Creating temporary marker at:', markerPos);

    // 새 임시 마커 생성
    const marker = new window.kakao.maps.Marker({
      position: new window.kakao.maps.LatLng(markerPos.lat, markerPos.lng),
      image: new window.kakao.maps.MarkerImage(
        markericon,
        new window.kakao.maps.Size(44, 44),
        { offset: new window.kakao.maps.Point(22, 44) }
      )
    });

    marker.setMap(mapRef.current);
    markerRef.current = marker;

    // 마커 클릭 이벤트
    const handleMarkerClick = () => {
      setOverlayVisible(true);
      setTempPlaceText(localStorage.getItem('placeText') || '');
      setTempPlaceKeyword(localStorage.getItem('placeKeyword') || '');
    };

    window.kakao.maps.event.addListener(marker, 'click', handleMarkerClick);

    // 클린업 함수
    return () => {
      if (marker) {
        window.kakao.maps.event.removeListener(marker, 'click', handleMarkerClick);
      }
    };
  }, [markerPos, mapLoaded]);

  const handleSearchClick = () => {
    console.log('Search button clicked from WeatherMap');
  };

  useEffect(() => {
    if (markerPos) {
      setPlaceText(localStorage.getItem('placeText') || '');
      setPlaceKeyword(localStorage.getItem('placeKeyword') || '');
    }
  }, [markerPos]);

  useEffect(() => {
    if (overlayVisible) {
      setTempPlaceText(localStorage.getItem('placeText') || '');
      setTempPlaceKeyword(localStorage.getItem('placeKeyword') || '');
    }
  }, [overlayVisible]);

  return (
    <Container>
      <CardContainer>
        <MapContainer>
          <MapDiv id="map" />
          {markerPos && overlayVisible && (
            <CustomOverlay>
              <PhotoButton onClick={() => fileInputRef.current && fileInputRef.current.click()}>
                <img src={photoinput} alt="photoinput" style={{width:102,height:102}} />
              </PhotoButton>
              <div style={{ width: 250, display: 'flex', flexDirection: 'column', alignItems: 'flex-start', justifyContent: 'flex-start' }}>
                <OverlayTextInput
                  value={tempPlaceText}
                  readOnly
                  placeholder="아직 등록 된 장소가 없어요!"
                />
                {tempPlaceKeyword && tempPlaceKeyword.trim() !== '' && (
                  <KeywordButton style={{ margin: 0 }}>{tempPlaceKeyword}</KeywordButton>
                )}
              </div>
              <input
                type="file"
                accept="image/*"
                ref={fileInputRef}
                style={{ display: 'none' }}
                onChange={e => {
                  if (e.target.files && e.target.files[0]) {
                    setSelectedFile(e.target.files[0]);
                  }
                }}
              />
              <OverlayActionButton onClick={() => navigate('/weather-write')}>
                등록하기
              </OverlayActionButton>
            </CustomOverlay>
          )}
        </MapContainer>
        <SearchBarRow>
            <SearchBarContainer>
                <SearchBar>
                <SearchText>장소를 검색해 보세요.</SearchText>
                <CircularButton onClick={handleSearchClick}>
                    <ButtonIcon><img src={weathersearchlogo} alt="weatersearch" style={{width:44,height:44,marginRight:5}} /></ButtonIcon>
                </CircularButton>
                </SearchBar>
            </SearchBarContainer>
            <WriteButton onClick={() => navigate('/weather-write')} />
        </SearchBarRow>
      </CardContainer>
    </Container>
  );
};

export default WeatherMap;
