import React from 'react';
import styled from 'styled-components';

const GalleryContainer = styled.div`
  flex: 1;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 8px;
  margin-bottom: 16px;
`;

const ImageItem = styled.div`
  background: ${props => props.bg || '#e0e0e0'};
  border-radius: 8px;
  height: 80px;
  background-size: cover;
  background-position: center;
  position: relative;
  cursor: pointer;

  &.large {
    grid-row: span 2;
    height: 168px;
  }

  &:hover {
    transform: scale(1.02);
    transition: transform 0.2s ease;
  }
`;

const ImageOverlay = styled.div`
  position: absolute;
  bottom: 8px;
  right: 8px;
  width: 24px;
  height: 24px;
  background: rgba(0, 0, 0, 0.6);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: 12px;
`;

const ImageGallery = () => {
  // 실제로는 props로 받아올 이미지 데이터
  const images = [
    { id: 1, bg: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', hasIcon: true },
    { id: 2, bg: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)' },
    { id: 3, bg: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)', large: true },
    { id: 4, bg: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)' },
    { id: 5, bg: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)', hasIcon: true },
    { id: 6, bg: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)' },
    { id: 7, bg: 'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)' },
    { id: 8, bg: 'linear-gradient(135deg, #a8caba 0%, #5d4e75 100%)', large: true }
  ];

  return (
    <GalleryContainer>
      {images.map(image => (
        <ImageItem 
          key={image.id}
          bg={image.bg}
          className={image.large ? 'large' : ''}
        >
          {image.hasIcon && (
            <ImageOverlay>
              ↗
            </ImageOverlay>
          )}
        </ImageItem>
      ))}
    </GalleryContainer>
  );
};

export default ImageGallery;
