import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import writebackground from '../assets/writebackground.png';
import { useNavigate, useLocation } from 'react-router-dom';



const Container = styled.div`
  width: 440px;
  height: 956px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url(${writebackground});
  background-size: cover;
  background-position: center;
`;

const CardContainer = styled.div`
  width: 440px;
  height: 956px;
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(2px);
`;

const CenterInput = styled.input`
  width: 320px;
  height: 56px;
  border-radius: 18px;
  border: none;
  background: transparent;
  font-size: 28px;
  padding: 0 20px;
  text-align: center;
  outline: none;
  color: #EBEBEB;
  &::placeholder {
    color: #aaa;
    font-weight: 400;
  }
`;

const KeywordLabelContainer = styled.div`
  position: absolute;
  bottom: 48px;
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: center;
  gap: 12px;
`;

const KeywordLabel = styled.div`
  width: 62px;
  height: 24px;
  flex-shrink: 0;
  margin: 0 40px;
  flex-wrap: wrap;
  border-radius: 12.5px;
  border: 0.7px solid ${props => props.active ? 'color(display-p3 0.3995 0.8083 0.747)' : props.color || '#aaa'};
  background: ${props => props.active ? 'color(display-p3 0.353 0.5707 0.5561 / 0.34)' : props.bg || 'rgba(200,200,200,0.18)'};
  backdrop-filter: blur(4.2px);
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.active ? 'color(display-p3 0.3995 0.8083 0.747)' : props.color || '#aaa'};
  font-size: 15px;
  font-weight: 400;
`;


const WeatherWriteKey = () => {
  return (
    <Container>
      <CardContainer>
        <CenterInput placeholder="이곳은 어떤 장소인가요?" fontSize={28}/>
        <KeywordLabelContainer>
          <KeywordLabel color="#FFB064" bg="rgba(255, 176, 100, 0.18)">신나는</KeywordLabel>
          <KeywordLabel color="#D6ECAB" bg="rgba(102, 222, 104, 0.18)">편안한</KeywordLabel>
          <KeywordLabel color="#4E94E8" bg="rgba(78, 148, 232, 0.18)">맛있는</KeywordLabel>
          <KeywordLabel color="#FF8767" bg="rgba(255, 135, 103, 0.18)">행복한</KeywordLabel>
          <KeywordLabel color="#B48CF2" bg="rgba(180, 140, 242, 0.18)">설레는</KeywordLabel>
          <KeywordLabel color="#F2C94C" bg="rgba(242, 201, 76, 0.18)">따뜻한</KeywordLabel>
          <KeywordLabel color="#2AD1BE" bg="rgba(42, 209, 190, 0.18)">상쾌한</KeywordLabel>
          <KeywordLabel color="#FF5E5E" bg="rgba(255, 94, 94, 0.18)">아쉬운</KeywordLabel>
          <KeywordLabel color="#8D8D8D" bg="rgba(141, 141, 141, 0.18)">지루한</KeywordLabel>
        </KeywordLabelContainer>
      </CardContainer>
    </Container>
  );
};

export default WeatherWriteKey;
